import React, { useState } from "react";

const ChatFooter = ({socket}) => {
  const [message, setMessage] = useState();
  const sendMessageHandler=()=>{
    if(message?.trim() && localStorage.getItem("userName")){
        socket.emit("message",{
            text:message,
            name:localStorage.getItem("userName"),
            socketID:socket.id,
            id:`${socket.id}${Math.random()}`
        });
    }
    setMessage("");
  }
  const handleTyping=()=>{
    socket.emit("typing",``)
  }
  return (
    <footer className="bg-slate-800 h-[70px]  flex items-center justify-center fixed w-[70%] md:w-[calc(100%-304px)] bottom-0">
      <form className="w-[100%] flex items-center justify-center">
        <input
        className="bg-transparent h-[40px] w-[85%] outline-none text-white text-md pl-2 pr-2"
          type="text"
          placeholder="Type message here"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          onKeyDown={handleTyping}
        />
        <button onClick={sendMessageHandler} className=" h-[20px] w-[30px] flex items-center justify-center "><img src="https://www.svgrepo.com/show/466173/send.svg" alt=""/></button>
      </form>
    </footer>
  );
};

export default ChatFooter;
