  return (
    <div className='fixed w-[70%] md:w-[calc(100%-304px)] z-10 bg-slate-800 flex h-[60px]  justify-between items-center pl-2 pr-2'>
        <div>
            <h3 className='font-bold text-md'>{name?? "Group Name"}</h3>
            <p className='text-sm font-mono italic'>{typingStatus}</p>
        </div>
        <div>
