import React, { useState, useEffect,useRef } from "react";
// import { useParams } from 'react-router-dom';
import ChatBar from "./ChatBar";
import ChatBody from "./ChatBody";
import ChatFooter from "./ChatFooter";
import ChatHeader from "./ChatHeader";

const PrivateChat = ({ socket }) => {
  
  const [active, setActive] = useState(false);
  const [roomer, setRoomer] = useState(null);
  const lastMessageRef=useRef();
  // const params=useParams();
  // const id=params.id;
  

  useEffect(()=>{
    // scroll to bottom for every text
    lastMessageRef.current?.scrollIntoView({behavior:"smooth"});
},[messages]);

  const roomerSetter = (data) => {
    setRoomer(data);
  };
  const activeSetter = () => {
    setActive(true);
  };
  return (
    <div className="bg-gray-950 text-white min-h-screen flex justify-between">
      <div className=" bg-slate-800 w-[29.5%] md:w-[300px]">
        <ChatBar
          socket={socket}
          roomerSetter={roomerSetter}
          activeSetter={activeSetter}
        />
      </div>
      <main className="bg-slate-900 w-[70%] md:w-[calc(100%-304px)]">
        <ChatHeader name={"private chat"} roomer={roomer} />
        <Priva active={active} messages={messages} lastMessageRef={lastMessageRef}/>
        <ChatFooter socket={socket} active={active} roomer={roomer} />
      </main>
    </div>
  );
};

export default PrivateChat;
