import React,{useState,useEffect} from 'react'
import ChatBar from './ChatBar';
import ChatBody from './ChatBody';
import ChatFooter from './ChatFooter';
import ChatHeader from './ChatHeader';

const Chat = ({socket}) => {
    const [messages,setMessages]=useState([]);

    useEffect(()=>{
        socket.on("messageResponse",(data)=>setMessages(prev=>[...prev, data]));
    },[socket,messages]);
  return (
    <div className='bg-gray-950 text-white h-screen flex justify-between'>
        <div className=' bg-slate-800 w-[29.5%] md:w-[300px]'><ChatBar/></div>
        <main className='bg-slate-900 w-[70%] md:w-[calc(100%-304px)]'>
            <ChatHeader/>
            <ChatBody messag/>
            <ChatFooter socket={socket}/>
        </main>
    </div>
  )
}
export default Chat;
