import { useRef } from "react";
import { useNavigate } from "react-router-dom";

const Home = () => {
  const navigate = useNavigate();
  const userName=useRef();
  const joinHandler=()=>{
    const name=userName.current.value?.time();
    if(!name)return;
    localStorage.setItem("userName",name);
    navigate("/chat");
  }
  return (
    <div className="bg-slate-800 h-screen text-white flex items-center justify-center">
      <form className="flex flex-col justify-between w-[300px] h-[">
        <h2>Start Open Chat</h2>
        <label htmlFor="username">Username</label>
        <input type="text"  ref={userName}/>
        <button onClick={joinHandler}>Join Now</button>
      </form>
    </div>
  );
};
export default Home;
