const fallback =
  "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTsLUBfsRtFGRe_GHeCOTEhilccWUrUZlLMbg&usqp=CAU";

const ChatBar = ({ socket, roomerSetter, activeSetter }) => {
  const [users, setUsers] = useState([]);
  // const navigate=useNavigate();
  useEffect(() => {
    socket.on("newUserResponse", (data) => {
      const privateChatUsers = data.filter((user) => user.private === true);
      const groupChatUsers = data.filter((user) => user.private === false);
      const filteredUsers = roomerSetter ? privateChatUsers : groupChatUsers;
      setUsers(filteredUsers)
    });
  }, [socket, users]);
  console.log(users);

  //  const switchRoomHandler=()=>{
  //   if(roomerSetter){
  //     navigate("/chat");
  //   }
  //   else navigate("/chat/private");
  //  }

  return (
    <div className="fixed flext flex-col items-center justify-center pt-2">
      <h1 className="text-2xl text-pink-800 font-mono font-bold flex items-center justify-center">
        Open Chat
      </h1>
      <div className="flex flex-col items-center  pt-5">
        <h3 className="text-green-700 text-md font-mono font-bold pb-4">
          ACTIVE USERS
        </h3>
        <div>
          {users.map((user) => (
            <UserCart
              key={user.id}
              id={user.id}
              userName={user.name}
              img={user.img || fallback}
              roomerSetter={roomerSetter}
              activeSetter={activeSetter}
            />
          ))}
        </div>
      </div>
      {/* <div className="flex items-center justify-center mt-10"><button className=" bg-rose-800 w-[80px] h-[35px] rounded-md font-mono font-bold text-md" onClick={switchRoomHandler}>Switch</button></div> */}
    </div>
  );
};

export default ChatBar;
