import React,{useState} from 'react';
// import { useParams } from 'react-router-dom';
import ChatBar from './ChatBar';
import ChatBody from './ChatBody';
import ChatFooter from './ChatFooter';
import ChatHeader from './ChatHeader';


const PrivateChat = ({socket}) => {
  const [messages,setMessages]=useState([]);
    const [active,setActive] = useState(false);
    const [roomer,setRoomer] =useState(null);
    // const params=useParams();
    // const id=params.id;
    useEffect(()=>{
      socket.on("messageResponse",(data)=>setMessages([...messages, data]));
  },[socket,messages]);
    const roomerSetter=(data)=>{
      setRoomer(data);
    }
    const activeSetter=()=>{
      setActive(true);
    }
  return (
    <div className='bg-gray-950 text-white min-h-screen flex justify-between'>
        <div className=' bg-slate-800 w-[29.5%] md:w-[300px]'><ChatBar socket={socket} roomerSetter={roomerSetter} activeSetter={activeSetter}/></div>
        <main className='bg-slate-900 w-[70%] md:w-[calc(100%-304px)]'>
            <ChatHeader name={"private chat"} roomer={roomer} />
            <ChatBody active={active}/>
            <ChatFooter socket={socket} active={active} roomer={roomer}/>
        </main>
    </div>
  )
}

export default PrivateChat;
